(()=>{"use strict";const e=e=>Math.ceil(e);document.addEventListener("DOMContentLoaded",void document.getElementById("searchButton").addEventListener("click",(t=>{t.preventDefault(),async function(){const t=await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${document.getElementById("searchBar").value}&APPID=d8b9f48dd6f09d896c01cd6aaf24c79b&units=imperial`,{mode:"cors"});if(!t.ok)throw new Error(`HTTP error ${t.status} `);const n=await t.json();var o,r,c,a,i;console.log(n),o=n.name,r=n.sys.country,document.querySelector("#cityname > h1").textContent=`${o}, ${r}`,c=n.main.temp,document.querySelector("#currentTemp > h1").textContent=`${e(c)}°F`,(e=>{document.querySelector("#currentDescription > h3").textContent=`${(e=>{const t=e.split(" ");for(let e=0;e<t.length;e+=1)t[e]=t[e][0].toUpperCase()+t[e].substr(1);return t.join(" ")})(e)}`})(n.weather[0].description),a=n.main.temp_min,i=n.main.temp_max,document.querySelector("#minMaxCurrentTemp > h3").textContent=`high: ${e(i)}°F min: ${e(a)}°F`,console.log(n.wind),document.getElementById("currentDisplay").style.display="block"}()})))})();